import {default as xdr} from "../generated/stellar-xdr_generated";
import {Keypair} from "../keypair";
import {StrKey} from "../strkey";

/**
 * Create a invoke operation.
 * @function
 * @alias ContrOperation.invoke
 * @param {object} opts
 * @param {string} opts.newState - new state.
 * @returns {xdr.Invoke}
 */
export const invoke = function(opts) {

  let attributes = {};
  attributes.newState        = opts.newState;
  let invoke             = new xdr.Invoke(attributes);

  let opAttributes = {};
  opAttributes.body = xdr.ContrOperationBody.invoke(invoke);

  return new xdr.ContrOperation(opAttributes);
};