import {default as xdr} from "../generated/stellar-xdr_generated";
import {Keypair} from "../keypair";
import {StrKey} from "../strkey";

/**
 * Check a Tx contains the contract ops.
 * @function
 * @alias Operation.checkContract
 * @param {object} opts
 * @param {string} opts.isContract - Check: is contract
 * @param {string} [opts.source] - The source account for the payment. Defaults to the transaction's source account.
 * @returns {xdr.CheckContractOp}
 */
export const checkContract = function(opts) {

  if (!this.isValidIsContract(opts.isContract)) {
    throw new TypeError(this.constructIsContractRequirementsError('isContract'));
  }

  let attributes = {};
  attributes.isContract = this._toXDRIsContract(opts.isContract);
  let checkContract          = new xdr.CheckContractOp(attributes);

  let opAttributes = {};
  opAttributes.body = xdr.OperationBody.checkContract(checkContract);
  this.setSourceAccount(opAttributes, opts);

  return new xdr.Operation(opAttributes);
};