import {default as xdr} from "../generated/stellar-xdr_generated";
import {Keypair} from "../keypair";
import {StrKey} from "../strkey";

/**
 * Create a query operation.
 * @function
 * @alias ContrOperation.query
 * @param {object} opts
 * @param {string} opts.data - query data.
 * @returns {xdr.Query}
 */
export const query = function(opts) {

  let attributes = {};
  attributes.newState        = opts.newState;
  let query             = new xdr.Query(attributes);

  let opAttributes = {};
  opAttributes.body = xdr.ContrOperationBody.query(query);

  return new xdr.ContrOperation(opAttributes);
};